  return (
    <div className='form'>
      <h1>Add Book</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder='Title'
          value={formData.title}
          onChange={handleChange}
        />
        <input
          type="text"
          name="disc"
          placeholder='Description'
          value={formData.disc}
          onChange={handleChange}
        />
        <input
          type="number"
          name="price"
          placeholder='Price'
          value={formData.price}
          onChange={handleChange}
        />
        <input
          type="text"
          name="cover"
          placeholder='Cover'
          value={formData.cover}
          onChange={handleChange}
        />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default Add;
