import React, { useState } from 'react';

function Add() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    cover: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    console.log(name)
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // You can do something with the formData here, such as sending it to a server or updating a state in a parent component.
    console.log(formData);
  };

  return (
    <div className='form'>
      <h1>Add Book</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder='Title'
          value={formData.title}
          onChange={handleChange}
        />
        <input
          type="text"
          name="description"
          placeholder='Description'
          value={formData.description}
          onChange={handleChange}
        />
        <input
          type="number"
          name="price"
          placeholder='Price'
          value={formData.price}
          onChange={handleChange}
        />
        <input
          type="text"
          name="cover"
          placeholder='Cover'
          value={formData.cover}
          onChange={handleChange}
        />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default Add;
