import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

import '../App.css';

function Books() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [error, setError] = useState(null);
  const [filterText, setFilterText] = useState('');

  useEffect(() => {
    const fetchAllBooks = async () => {
      try {
        const res = await axios.get("http://localhost:3001/");
        setData(res.data);
        setFilteredData(res.data); // Initialize filteredData with all books
      } catch (err) {
        console.error("Error fetching books:", err);
        setError("Error fetching books. Please check the server.");
      }
    };

    fetchAllBooks();
  }, []);

  const handledelete = async (id) => {
    console.log(id);
    try {
      await axios.delete("http://localhost:3001/books/" + id);
      window.location.reload();
      alert("Book Was Deleted !");
    } catch (err) {
      console.error("Error deleting book:", err);
      setError("Error deleting the book. Please check the server.");
    }
  };

  // Automatically filter books as you type in the input field
  useEffect(() => {
    const filteredBooks = data.filter((book) =>
      book.title.toLowerCase().includes(filterText.toLowerCase())
    );
    console.log(f)
    setFilteredData(filteredBooks);
  }, [data, filterText]);

  return (
    <div className="mainpage">
      <h1>Books Available in Store!</h1>
      <input
        type="text"
        placeholder="Search by book name"
        value={filterText}
        onChange={(e) => setFilterText(e.target.value)}
      />
      <div className="main">
        {filteredData.map((item, i) => (
          <div className="card" key={i}>
            <img src={`http://localhost:3001/images/` + item.cover} alt="" />
            <h3>{item.title}</h3>
            <p>{item.disc}</p>
            <p>
              <b>Rs {item.price}/=</b>
            </p>
            <button className="delete" onClick={() => handledelete(item.id)}>
              Delete
            </button>
            <button className="update">
              <Link to={`/Edit/${item.id}`}>Update</Link>
            </button>
          </div>
        ))}
      </div>
      <button>
        <Link to="/Add">Add Book</Link>
      </button>
    </div>
  );
}

export default Books;
